(1, 1, 1565536174, 1615819751, 'Thiết kế mọi thứ', 'upload/images/banner/banner-think-designer.jpg', '<p>Thiết kế mọi thứ, s&aacute;ng tạo mọi nơi...</p>\r\n', '', 1, '', 1, 0),
(3, 1, 1600267042, 1600267042, 'banner sidebar 1', 'upload/images/banner/2e7abbd9d2042d5a7415.jpg', '', '', 7, '', 1, 0),
(4, 1, 1600267066, 1600267066, 'banner sidebar 2', 'upload/images/banner/d46f58cc3111ce4f9700.jpg', '', '', 8, '', 1, 0),
(5, 1, 1615911300, 1615911300, 'Mỹ Lệ – Nhân Sâm Hoàng Diệu', 'upload/images/banner/review/KHACH-HANG-THINK-DESIGNER-sam-hoang-dieu-90x90.jpg', 'Mỹ Lệ - Marketing', '', 6, '<p>Think Designer l&agrave; đơn vị thiết kế website rất chuy&ecirc;n nghiệp v&agrave; uy t&iacute;n. V&agrave; đặc biết l&agrave;m việc rất nhanh gọn v&agrave; hiệu quả, T&ocirc;i đ&atilde; tạo ở đ&acirc;y một website b&aacute;n h&agrave;ng cho m&igrave;nh, t&ocirc;i rất h&agrave;i l&ograve;ng.</p>\r\n', 1, 0),
(6, 1, 1615911345, 1615911345, 'Xuân Hồng – Sanaderm', 'upload/images/banner/review/KHACH-HANG-THINK-DESIGNER-XUAN-HONG-SANADERM-90x90.jpg', 'Xuân hồng - C.E.O', '', 6, '<p>T&ocirc;i đến v&agrave; cộng t&aacute;c l&agrave;m việc c&ugrave;ng Think Designer như những g&igrave; tự nhi&ecirc;n vốn c&oacute;. T&ocirc;i thật mai mắn khi t&igrave;m được đơn vị t&acirc;m huyết, y&ecirc;u nghề, trẻ năng động&hellip; Kh&aacute;t khao vướng tới. C&aacute;c bạn thật tuyệt vời, thiết kế đẹp, nhanh ch&oacute;ng, hiệu quả. Ch&uacute;c tất cả th&agrave;nh vi&ecirc;n Think Designer tiến xa hơn&hellip;</p>\r\n', 1, 0),
(7, 1, 1615911385, 1615911385, 'Phạm Đạt – FBNC', 'upload/images/banner/review/KHACH-HANG-THINK-DESIGNER-FBNC-DAT-PHAM-90x90.jpg', 'Phạm Đạt - Trưởng phòng truyền thông', '', 6, '<p>Think Designer với đội ngũ nh&acirc;n vi&ecirc;n rất nhiệt huyết l&agrave;m đến nơi v&agrave; lu&ocirc;n t&acirc;m huyết từng sản phẩm m&agrave; họ tạo ra, t&ocirc;i từng l&agrave;m việc c&ugrave;ng Think Designer t&ocirc;i đ&aacute;nh gi&aacute; cao từng th&agrave;nh vi&ecirc;n của Think Designer v&agrave; trang website http://fbncvietnam.com/ được Think Designer thiết kế lập tr&igrave;nh n&ecirc;n.</p>\r\n', 1, 0),
(8, 1, 1615989327, 1615989327, 'Đối tác 1', 'upload/images/banner/brand/LOGO-fbnc-tin-tuc.png', '', '', 9, '', 1, 0),
(9, 1, 1615989345, 1615989345, 'Đối tác 2', 'upload/images/banner/brand/LOGO-calapharco.png', '', '', 9, '', 1, 0),
(10, 1, 1615989357, 1615989357, 'Đối tác 3', 'upload/images/banner/brand/LOGO-nguoi-quang-phia-nam.png', '', '', 9, '', 1, 0),
(11, 1, 1615989357, 1615989768, 'Đối tác 3', 'upload/images/banner/brand/LOGO-fbnc-tin-tuc.png', '', '', 9, '', 1, 0),
(12, 1, 1615989367, 1615989367, 'Đối tác 4', 'upload/images/banner/brand/LOGO-sam-hoang-dieu.png', '', '', 9, '', 1, 0),
(13, 1, 1615989375, 1615989375, 'Đối tác 4', 'upload/images/banner/brand/LOGO-XUAN-HONG-SANADERM.png', '', '', 9, '', 1, 0),
(14, 1, 1616080949, 1616081520, 'CEO', 'upload/images/template/news/thanh-vien-think-designer-Chairman-CEO.jpg', 'Chairman & CEO. Designer.\r\n', '', 10, '<p><strong>Think Designer</strong>&nbsp;đ&atilde; x&acirc;y dựng một quy tr&igrave;nh chuy&ecirc;n nghiệp về sản xuất sản phẩm, từ việc tư vấn giải ph&aacute;p sales kit đến việc bi&ecirc;n tập, viết nội dung, chụp h&igrave;nh ảnh v&agrave; thiết kế s&aacute;ng tạo. Ch&uacute;ng t&ocirc;i đ&atilde; triển khai nhiều dự &aacute;n th&agrave;nh c&ocirc;ng v&agrave; ng&agrave;y c&agrave;ng được nhiều kh&aacute;ch h&agrave;ng t&iacute;n nhiệm.</p>\r\n', 1, 0),
(15, 1, 1616083064, 1616083130, 'Nguyễn Hải', 'upload/images/member/thanh-vien-think-designer-Chairman-CEO.jpg', 'Chairman & CEO. Designer.', '', 11, '<p>Được s&aacute;ng lập bởi sự kết hợp của những chuy&ecirc;n gia c&oacute; chuy&ecirc;n m&ocirc;n v&agrave; kinh nghiệm trong lĩnh vực Marketing, c&ugrave;ng với đội ngũ Copywriter, Designer, Photographer chuy&ecirc;n nghiệp đ&aacute;p ứng mọi nhu cầu của doanh nghiệp v&agrave; tạo n&ecirc;n những sản phẩm ấn tượng, hiệu quả.</p>\r\n', 1, 0),
(16, 1, 1616083118, 1616083118, 'Kiều Nguyễn', 'upload/images/member/thanh-vien-think-designer-khac-kieu.jpg', 'C.E.O', '', 11, '<p>Kiều Nguyễn l&agrave; trong những th&agrave;nh viện của Hội Đồng Hương Quảng Nam. Cũng l&agrave; chủ bi&ecirc;n của thương hiệu &ldquo;Người Quảng Ph&iacute;a Nam&rdquo;. Kiều Nguyễn tin rằng sẽ mang lại sự trải nghiệm tốt nhất m&agrave; dịch vụ Think Designer mang lại.</p>\r\n', 1, 0),
(17, 1, 1616233853, 1616233853, 'Justin Nguyễn', 'upload/images/banner/member/thanh-vien-think-designer.jpg', 'Designer', '', 11, '<p>Justin Nguyễn l&agrave; một trong những Designer của Think Designer được rất nhiều kh&aacute;ch h&agrave;ng y&ecirc;u th&iacute;ch v&agrave; t&iacute;n hiệm từng học tại Đại Học Hoa Sen. Chắc chắn sẽ mang đến c&aacute;i đẹp cho qu&yacute; kh&aacute;ch h&agrave;ng.</p>\r\n', 1, 0),
(18, 1, 1616234034, 1616234034, 'Đoàn Kiều', 'upload/images/banner/member/thanh-vien-think-designer-doan-kieu.jpg', 'Copywriter', '', 11, '<p>Đo&agrave;n Kiều l&agrave; một những th&agrave;nh vi&ecirc;n Think Designer hơn 5 năm trong lĩnh vực Copywriter hứa hẹn sẽ mạng lại cho qu&yacute; kh&aacute;ch h&agrave;ng nhiều sự kh&aacute;c biệt.</p>\r\n', 1, 0),
(19, 1, 1616234034, 1616234034, 'Đoàn Kiều', 'upload/images/banner/member/thanh-vien-think-designer-doan-kieu.jpg', 'Copywriter', '', 11, '<p>Đo&agrave;n Kiều l&agrave; một những th&agrave;nh vi&ecirc;n Think Designer hơn 5 năm trong lĩnh vực Copywriter hứa hẹn sẽ mạng lại cho qu&yacute; kh&aacute;ch h&agrave;ng nhiều sự kh&aacute;c biệt.</p>\r\n', 1, 0);

